import React, { useState } from "react";
import FirstForm from "./Form/FirstForm";
import SecondForm from "./Form/SecondForm";
import "./Resume.css";

const Resume = () => {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
      // Initialize form data here
      // For example:
      firstName: '',
      lastName: '',
      email: '',
      // ... other fields for the first form
      companyName: '',
      role: '',
      startDate: '',
      endDate: '',
      description: '',
      // ... other fields for the second form
    });

    const nextStep = () => {
      setStep((prevStep) => prevStep + 1);
    };

    const prevStep = () => {
      setStep((prevStep) => prevStep - 1);
    };

    const handleChange = (name, value) => {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
      }));
    };

    // Render the appropriate form based on the current step
    const renderForm = () => {
      switch (step) {
        case 1:
          return <FirstForm formData={formData} handleChange={handleChange} />;
        case 2:
          return <SecondForm formData={formData} handleChange={handleChange} />;
        // Add more cases for additional steps if needed
        default:
          return null;
      }
    };

    return (
      <div>
        <h2>Multi-Step Form</h2>
        {renderForm()}
        <div>
          {step > 1 && (
            <button onClick={prevStep} type="button">
              Previous
            </button>
          )}
          {step < 2 ? (
            <button onClick={nextStep} type="button">
              Next
            </button>
          ) : (
            <button type="submit">Submit</button>
          )}
        </div>
      </div>
    );
  };

export default Resume;
