import React, { useState } from "react";
import './Right.css';
import { Avatar } from "@mui/material";

function Right() {
  const [showMenu, setShowMenu] = useState(false);

  const handleButtonClick = () => {
    setShowMenu(!showMenu);
  };

  const handleOptionClick = (option) => {
    console.log(`Selected option: ${option}`);
    setShowMenu(false);
  };

  return (
    <>
      <div className="review-container">
        <header>
          <p>select templates <span>|</span> - Aa +</p>
          <div className="dropdown">
            <button onClick={handleButtonClick}>Download PDF</button>
            {showMenu && (
              <div className="dropdown-content">
                <div onClick={() => handleOptionClick("Export to Doc")}>Export to Doc</div>
                <div onClick={() => handleOptionClick("Export to TXT")}>Export to TXT</div>
                <div onClick={() => handleOptionClick("Share")}>Share</div>
              </div>
            )}
          </div>
          <Avatar />
        </header>
        <main>
          <div className="profile-section">
          <div className="profile">
          <div class="circle-frame">
            <img src="https://images.pexels.com/photos/220453/pexels-photo-220453.jpeg?auto=compress&cs=tinysrgb&w=600" alt="..."/>
          </div>
            <h3 className="name">Sarthak Sharma</h3>
            <p className="designation">Product Engineer</p>
          </div>

          <div className="A-P-E-section">
            <h3 className="details-head">Address</h3>
            <p className="current-location">Odisha, India</p>
            <p className="phone">Phone</p>
            <p className="ph-no">+91 9078105059</p>
            <p className="email">xyz@gmail.com</p>
          </div>

          <div className="social-links">
          <h3 className="details-head">Links</h3>
            <p className="links">GitHub</p>
            <p className="links">LinkedIn</p>
            <p className="links">LeetCode</p>          
          </div>

          <div className="skills">
            <h3 className="details-head">Skills</h3>
            <p className="added-skills">Figma</p>
            <p className="added-skills">Java</p>
            <p className="added-skills">C</p>
            <p className="added-skills">C++</p>
          </div>
          </div>
          <div className="profile-detailed-section"></div>
        </main>
      </div>
    </>
  );
}

export default Right;
